(function(){const version='0.3.1';const _defaults={text:'Quick Header',textAlign:'center',size:48,weight:400,top:'0rem',right:'.5rem',bottom:'.25rem',left:'.5rem',colour:'#000',lineColour:'#000',lineWidth:'100%',lineAlign:'center',};const numberUnit=(value,unit)=>{value=value.toString();let sizeNumber=value.match(/[\.]?[\d,]+\.?\d?/)?.[0]||'';let sizeUnit=value.replace(/\d+/g,'').replace('.','')||unit;return sizeNumber.concat(sizeUnit);}
class QuickHeader extends HTMLElement{static get observedAttributes(){return['text','textalign','size','colour','weight','top','right','bottom','left','underline','linecolour','linewidth','linealign','inline'];}
constructor(){super();Object.defineProperty(this,'VERSION',{value:version,writable:false});Object.defineProperty(this.constructor,'VERSION',{value:version,writable:false});let shadow=this.attachShadow({mode:'closed'});this.shadow=shadow;const text=document.createElement('div');const line=document.createElement('hr');const style=document.createElement('style');text.classList.add('qh-text');line.classList.add('qh-line');shadow.appendChild(style);shadow.appendChild(text);shadow.appendChild(line);this.watch('textContent',(data)=>{this.setAttribute('text',data.value);});}
get text(){return this.getAttribute('text')||this.textContent||_defaults.text;}
get textAlign(){let align=this.getAttribute('textalign');if(!['left','right','initial'].includes(align))align=_defaults.textAlign;return align;}
get size(){return numberUnit(this.getAttribute('size')||_defaults.size,'px');}
get weight(){let weight=this.getAttribute('weight')||_defaults.weight;if(!['bold','bolder','lighter','normal','100','200','300','400','500','600','700','800','900'].includes(weight))weight=_defaults.weight;return weight;}
get colour(){return this.getAttribute('colour')||_defaults.colour;}
get top(){return numberUnit(this.getAttribute('top')||_defaults.top,'rem');}
get right(){return numberUnit(this.getAttribute('right')||_defaults.right,'rem');}
get bottom(){return numberUnit(this.getAttribute('bottom')||_defaults.bottom,'rem');}
get left(){return numberUnit(this.getAttribute('left')||_defaults.left,'rem');}
get underline(){return this.hasAttribute('underline')?'block':'none';}
get inline(){return this.hasAttribute('inline')?'inline-block':'block';}
get lineColour(){return this.getAttribute('linecolour')||_defaults.lineColour;}
get lineWidth(){return numberUnit(this.getAttribute('linewidth')||_defaults.lineWidth,'%');}
get lineAlign(){let align=this.getAttribute('linealign');if(!['left','right','initial'].includes(align))align=_defaults.lineAlign;return align;}
connectedCallback(){if(!this.hasAttribute('linealign'))this.setAttribute('linealign',_defaults.lineAlign);if(!this.hasAttribute('linecolour'))this.setAttribute('linecolour',_defaults.lineColour);this.updateStyle();this.updateText();}
disconnectedCallback(){}
adoptedCallback(){}
attributeChangedCallback(name,oldValue,newValue){console.log('attributeChangedCallback',name,oldValue,newValue);if(name=='text')this.updateText();else this.updateStyle();}
updateStyle(){this.shadow.querySelector('style').textContent=`
:host {
    all: initial;
    display: ${this.inline};
    font-family: inherit;
    contain: content;
}
:host-context(i) .qh-text {
    font-style: italic;
}
:host-context(.theme) .qh-text {
    font-style: italic;
}
.qh-text {
    margin: ${this.top} ${this.right} ${this.bottom} ${this.left};
    font-size: ${this.size};
    font-weight: ${this.weight};
    color: ${this.colour};
    text-align: ${this.textAlign};
    cursor: default;
    // -webkit-user-select: none;
    // -moz-user-select: none;
    // -ms-user-select: none;
    // user-select: none;
}
.qh-line {
    display: ${this.underline};
    float: ${this.lineAlign};
    height: 1px;
    width: ${this.lineWidth};
    margin-top: 0px;
    color: ${this.lineColour};
    background: ${this.lineColour};
}`;}
updateText(){if(this.textContent!=this.text)this.textContent=this.text;this.shadow.querySelector('div').textContent=this.text;}}
customElements.define('quick-header',QuickHeader);})();